function Person (name, surname, height, weight, age, sex, skinTone, eyeColor, hairColor){
    this.name = name
    this.surname = surname
    this.height = height
    this.weight = weight
    this.age = age
    this.sex = sex
    this.skinTone = skinTone
    this.eyeColor = eyeColor
    this.hairColor = hairColor
}

module.exports = Person